tippy('[tippy-title]', {
    arrow: true,
    placement: 'top',
    arrowType: 'round',
    duration: 250,
    animation: 'shift-away',
    theme: 'seogun',
    content(reference) {
        const title = reference.getAttribute('tippy-title');
        reference.removeAttribute('tippy-title');
        return title;
    },
});
